import React, {useState} from "react";
import Editor from "./Editor";


function App() {
 const [html,setHtml] =useState('')
 const [css,setCss] =useState('')
 const [js,setJs] =useState('')

 const srcDoc=`
 <html>
    <body>${html}</body>
    <style>${style}</style>
    <script>${script}</script>
 </html>
 `
  return (
    <>
      <div className="pane top-pane">
        <Editor 
        langauge="xml"
        displayName="HTML"
        onChange={setHtml}
        value={html}

        />
        <Editor
        langauge="css"
        displayName="CSS"
        onChange={setCss}
        value={css}

        />
        <Editor 
        lanagauge="javascript"
        displayName="JavaScript"
        value={js}
        onChange={setJs}
        />
      </div>
      <div className="pane">
        <iframe id="frame"
          srcDoc={srcDoc}
          title="Output"
          sandbox="allow-scripts"
          frameBorder="0"
          width="100%"
          height="100%"
        />
        </div>
    </>
  );
}

export default App;
