import React, {useState} from "react";
import Editor from "./Editor";


function App() {
 const [html,setHtml] =useState('')
  return (
    <>
      <div className="pane top-pane">
        <Editor 
        langauge="xml"
        displayName="HTML"
        onChange={setHtml}
        value={html}

        />
        <Editor
        langauge="css"
        />
        <Editor />
      </div>
      <div className="pane">
        <iframe
          title="Output"
          sandbox="allow-scripts"
          frameBorder="0"
          width="100%"
          height="100%"
        />
      </div>
    </>
  );
}

export default App;
